    let charactersticsExpanded = [];
    let ocPrice;
    let rcPrice;
    let ocPriceStrike;
    let rcPriceStrike;
    let durationLength;
    const durationAcronym = viewProps?.durationAcronym;
    let commitmentDuration;
    let promotionID;
    let promotionState;
    let showInfoIcon = false;
    let showDiscountIcon = false;
    let placeIdExist = false;
    productOfferingResponse?.forEach(offers => {
        offers?.productOfferingCharacteristic?.forEach(characterstics => {
            if (characterstics?.characteristicsMetadata) {
                characterstics?.characteristicsMetadata?.forEach(metadata => {
                    if (metadata?.name === 'VisibleInHighlights' && metadata?.value.toLocaleLowerCase() === "true") {
                        if (characterstics?.value?.length === 1) charactersticsCollapsed.push({ name: characterstics?.value?.[0]?.localizedValue });
                        else {
                            characterstics?.value?.forEach(value => {
                                if (value.default === true) {
                                    charactersticsCollapsed.push({ name: value.localizedValue });
                                }
                            });
                        }
                    }
                    if (metadata?.name === 'Visible' && metadata?.value.toLocaleLowerCase() === "true") {
                        if (characterstics?.value?.length === 1) charactersticsExpanded.push({ name: characterstics?.value?.[0]?.localizedValue });
                        else {
                            characterstics.value?.forEach(value => {
                                if (value.default === true) {
                                    charactersticsExpanded.push({ name: value.localizedValue });
                                }
                            });
                        }
                    }
                })
            }
        });

        offers?.productOfferingTotalPrice?.forEach(totalPrice => {
            for (let acronym in durationAcronym) {
                let recurringChargePeriod = totalPrice?.recurringChargePeriod;
                if (acronym === recurringChargePeriod) {
                    let recurringChargePeriodLength = totalPrice?.recurringChargePeriodLength;
                    if (recurringChargePeriodLength > 1) {
                        //durationLength = recurringChargePeriodLength + ' ' + frequecy;
                        let amountFrequrency;
                        if (recurringChargePeriod === 'month') {
                            amountFrequrency = '/mo'
                        }
                        else if (recurringChargePeriod === 'week') {
                            amountFrequrency = '/wk'
                        }
                        else {
                            amountFrequrency = '/day'
                        }
                        durationLength = recurringChargePeriodLength + ' ' + amountFrequrency;
                        //durationLength = recurringChargePeriodLength + ' ' + ((recurringChargePeriod === 'month') ? '/mo' : '/wk');
                    } else {
                        if (recurringChargePeriod === 'month') {
                            durationLength = '/mo'
                        }
                        else if (recurringChargePeriod === 'week') {
                            durationLength = '/wk'
                        }
                        else {
                            durationLength = '/day'
                        }
                        //durationLength = ((recurringChargePeriod === 'month') ? '/mo' : '/wk');

                    }
                    break;
                }
            }

            if (totalPrice?.priceType === 'OC') {
                ocPrice = currencySymbol + totalPrice.finalPrice.dutyFreeAmount.value;
                ocPriceStrike = currencySymbol + totalPrice.price.dutyFreeAmount.value
            }
            if (totalPrice?.priceType === 'RC') {
                rcPrice = currencySymbol + totalPrice.finalPrice.dutyFreeAmount.value + durationLength;
                rcPriceStrike = currencySymbol + totalPrice.price.dutyFreeAmount.value + durationLength;
            }
            totalPrice?.priceAlteration?.forEach(alteration => {
                if (alteration?.alterationType === 'Promotion Price Alteration') {
                    showDiscountIcon = true;
                }
            })
        });

        offers?.productOfferingTerm?.forEach(offeringTerm => {
            if (offeringTerm.default === true) {
                commitmentDuration = offeringTerm.localizedName;
            }
        });

        offers?.immediatePromotion?.forEach(promotion => {
            if (promotion.state === 'applied') {
                promotionID = promotion.promotion.id;
                promotionState = promotion.state;
            }
        });

        offers?.productOfferingPrice?.forEach(offeringPrice => {
            offeringPrice?.price?.impactingPriceParameter?.forEach(impactingPrice => {
                if (impactingPrice?.type === 'occurrences') {
                    showInfoIcon = true;
                }
            });
        });

        offers?.metadata?.forEach(metadata => {
            if (metadata.name === 'ppu' && metadata.value.toLocaleLowerCase() === 'true') {
                placeIdExist = true;
            }
        })
        let offerType = offers?.metadata?.filter(i => i?.type === "contractedOffer")
        let offerMetadata
        if (offerType?.length != 0) offerMetadata = true
        else offerMetadata = false
        offerData.push({
            id: offers?.id,
            icon: (offers.attachment && offers.attachment[0]) ? offers.attachment[0].url : ' ',
            name: offers?.localizedName,
            ocPrice: ocPrice + ' OC',
            ocPriceStrike: (ocPrice === ocPriceStrike) ? '' : ocPriceStrike,
            rcPrice: rcPrice,
            rcPriceStrike: (rcPrice === rcPriceStrike) ? '' : rcPriceStrike,
            description: offers?.description,
            offerPeriod: commitmentDuration ? commitmentDuration + ' ' + 'Commitment' : '',
            charactersticsCollapsed: charactersticsCollapsed,
            charactersticsExpanded: charactersticsExpanded,
            showPromotionsTab: promotionState ? ((promotionState === 'applied') ? false : true) : false,
            showOfferIcon: (offers.attachment && offers.attachment[0] && offers.attachment[0].url) ? true : false,
            promotionId: promotionID,
            showInfoIcon: showInfoIcon,
            immediatePromotionPresent: (offers.immediatePromotion ? true : false),
            showPromotionIcon: (offers.immediatePromotion ? true : false),
            showDisountIcon: showDiscountIcon,
            sendPlaceId: placeIdExist,
            prodOfferType: offers?.productOfferingType,
            contractedOffer: offerType?.[0]?.value,
            offerMetadata
        });
        charactersticsCollapsed = [];
        charactersticsExpanded = [];
        showInfoIcon = false;
        showDiscountIcon = false;
        placeIdExist = false
    });

    return offerData;
}
